function start_spinner(){var bg=document.querySelector('#spinner_bg');bg.style.display="block"}function stop_spinner(){var bg=document.querySelector('#spinner_bg');bg.style.display="none"}window.onload=function(){var spin_apng="data:image/svg+xml;base64,";var bg=document.createElement("div");bg.innerHTML='<img src="'+spin_apng+'" alt="Loading..." style="position: absolute;top: 50%;margin-top: -100px;margin-left: -100px;">';bg.id='spinner_bg';bg.style.position="fixed";bg.style.top="0px";bg.style.bottom="0px";bg.style.left="0px";bg.style.right="0px";bg.style.background="rgba(50,50,50,0.8)";bg.style.display="none";bg.style.cursor="wait";bg.style.textAlign="center";bg.style.zindex="10000";var element=document.querySelector('body');element.appendChild(bg)};